INSERT INTO `visuel` VALUES (1,'creationOriginale','Création\r\noriginale','>>>    Images animées de très haute qualité, réalisées sur mesure en fonction de vos souhaits de scénario et de contenu.\r\n\r\n>>>    Ci-dessous choisissez la durée voulue de la projection.',6000),(2,'pretJammer','Prêt à \r\njammer','>>>    Séquences d\'images animées inédites de haute qualité d’une durée de 1 minute chacune. \r\n\r\nUne ou plusieurs séquences forment une boucle complète qui se répète sur la durée voulue. \r\n\r\nEnsuite, vous choisissez :\r\n\r\n(1) les séquences dans une bibliothèque d’images animées,\r\n  \r\n(2) les couleurs dominantes souhaitées. \r\n\r\nRapide à mettre en oeuvre, économique et original.\r\n\r\n>>>    Ci-dessous choisissez le nombre de boucle(s) souhaitée(s).',2500),(3,'performanceArt','Performance\r\nart','>>>    Performance d’artiste(s) réalisé en projection lumière sur un édifice. \r\n\r\nElle peut n’être exécutée qu’une fois ou réitérée, s’appuyer ou non sur un scénario ou être improvisée. \r\n\r\nLe collectif Jam Project réunit des artistes de divers horizons (streets artist, plasticiens, photographe, danseurs, etc.) réalisant ce type de performance totalement inédite.',3500);
